SELECT SCH_SEQ, CNSLR_EMP_ID, SCH_DT, SCH_TM, SCH_TY_CD, SCH_CNTN, DEL_YN, DEL_DT, REG_EMP_ID, UPD_EMP_ID, REG_DT, UPD_DT FROM TB_CNSLR_SCHEDULE WHERE DEL_YN = 'N'
AND CNSLR_EMP_ID = #{searchCondition.cnslrEmpId}
AND SCH_DT = #{searchCondition.schDt}
AND SCH_TY_CD = #{searchCondition.schTyCd}
AND SCH_DT >= #{searchCondition.startDate}::date
AND SCH_DT <= #{searchCondition.endDate}::date
ORDER BY SCH_DT ASC, SCH_TM ASC LIMIT #{size} OFFSET #{offset}

