SELECT SCH_SEQ, CNSLR_EMP_ID, SCH_DT, SCH_TM, SCH_TY_CD, SCH_CNTN, DEL_YN, DEL_DT, REG_EMP_ID, UPD_EMP_ID, REG_DT, UPD_DT FROM TB_CNSLR_SCHEDULE WHERE CNSLR_EMP_ID = #{cnslrEmpId,jdbcType=VARCHAR}
AND SCH_DT = #{schDt,jdbcType=DATE}
AND DEL_YN = 'N' ORDER BY SCH_DT ASC, SCH_TM ASC
