mapper.select:
SELECT m1.MNU_CD, m1.MNU_NM, m1.MNU_URL, m1.MNU_DESC, m1.MNU_ORD, m1.MNU_LVL, m1.P_MNU_CD, m1.MNU_USE_YN, m1.MNU_AUTH_TYPE, m1.REG_EMP_ID, m1.UPD_EMP_ID, m1.DEL_YN, m1.DEL_DATE, m1.REG_DATE, m1.UPD_DATE, m2.MNU_NM as P_MNU_NM FROM TB_MNU_LST m1 LEFT JOIN TB_MNU_LST m2 ON m1.P_MNU_CD = m2.MNU_CD WHERE m1.DEL_YN = 'N'
AND (m1.MNU_NM LIKE CONCAT('%', #{searchKeyword}, '%') OR m1.MNU_DESC LIKE CONCAT('%', #{searchKeyword}, '%'))
AND m1.MNU_LVL = #{mnuLvl}
AND m1.MNU_USE_YN = #{mnuUseYn}
AND m1.MNU_AUTH_TYPE = #{mnuAuthType}
ORDER BY m1.MNU_LVL ASC, m1.MNU_ORD ASC LIMIT #{limit} OFFSET #{offset}

SELECT COUNT(*)
FROM TB_MNU_LST m1
WHERE m1.DEL_YN = 'N'

    AND (m1.MNU_NM LIKE CONCAT('%', #{searchKeyword}, '%') 
         OR m1.MNU_DESC LIKE CONCAT('%', #{searchKeyword}, '%'))


    AND m1.MNU_LVL = #{mnuLvl}


    AND m1.MNU_USE_YN = #{mnuUseYn}


    AND m1.MNU_AUTH_TYPE = #{mnuAuthType}
