mapper.select:
SELECT RGST_DT, SEQ, EMP_ID, RGST_TM, RGST_EMP_ID, UPDT_DT, UPDT_TM, UPDT_EMP_ID FROM TB_NTI_TRSM_TRGT_DTL
AND RGST_DT = #{rgstDt}
AND SEQ = #{seq}
AND EMP_ID = #{empId}
AND RGST_EMP_ID = #{rgstEmpId}
ORDER BY RGST_DT DESC, SEQ DESC, EMP_ID

mapper.update:
UPDATE TB_NTI_TRSM_TRGT_DTL
SET 
    UPDT_DT = #{updtDt},
    UPDT_TM = #{updtTm},
    UPDT_EMP_ID = #{updtEmpId}
WHERE RGST_DT = #{rgstDt} AND SEQ = #{seq} AND EMP_ID = #{empId}
