mapper.select:
SELECT LOG_SEQ, LOG_LEVEL, LOG_TYPE, LOG_MESSAGE, LOG_DETAIL, CLASS_NAME, METHOD_NAME, LINE_NUMBER, STACK_TRACE, EMP_ID, SESSION_ID, REQUEST_URI, REQUEST_METHOD, REQUEST_PARAMS, RESPONSE_STATUS, EXECUTION_TIME, IP_ADDRESS, USER_AGENT, ERROR_CODE, ERROR_CATEGORY, CREATED_DATE, CREATED_BY FROM TB_SYSTEM_LOG
AND LOG_LEVEL = #{logLevel}
AND LOG_TYPE = #{logType}
AND EMP_ID = #{empId}
AND ERROR_CODE = #{errorCode}
AND CREATED_DATE >= #{startDate}
AND CREATED_DATE <= #{endDate}
AND ( LOG_MESSAGE LIKE CONCAT('%', #{searchKeyword}, '%') OR LOG_DETAIL LIKE CONCAT('%', #{searchKeyword}, '%') OR CLASS_NAME LIKE CONCAT('%', #{searchKeyword}, '%') )
ORDER BY LOG_SEQ DESC LIMIT #{limit} OFFSET #{offset}

SELECT COUNT(*)
FROM TB_SYSTEM_LOG


        AND LOG_LEVEL = #{logLevel}


        AND LOG_TYPE = #{logType}


        AND EMP_ID = #{empId}


        AND ERROR_CODE = #{errorCode}


        AND CREATED_DATE >= #{startDate}


        AND CREATED_DATE <= #{endDate}


        AND (
            LOG_MESSAGE LIKE CONCAT('%', #{searchKeyword}, '%')
            OR LOG_DETAIL LIKE CONCAT('%', #{searchKeyword}, '%')
            OR CLASS_NAME LIKE CONCAT('%', #{searchKeyword}, '%')
        )

SELECT LOG_LEVEL, LOG_TYPE, ERROR_CODE, COUNT(*) as ERROR_COUNT, DATE(CREATED_DATE) as ERROR_DATE FROM TB_SYSTEM_LOG WHERE LOG_LEVEL = 'ERROR'
AND CREATED_DATE >= #{startDate}
AND CREATED_DATE <= #{endDate}
GROUP BY LOG_LEVEL, LOG_TYPE, ERROR_CODE, DATE(CREATED_DATE) ORDER BY ERROR_COUNT DESC
